clear all, close alltic;#Initialisation :t0 = 0;tf = 365;dt = 0.1;n = tf/dt;N0 = 4; #μ.mole.N.l.^-1P0 = 2.5; #μ.mole.N.l.^-1Z0 = 0.5; #μ.mole.N.l.^-1t = t0;N = N0; #Abondance d'azote (en μ.mole.N.l.^-1) et à t = 0P = P0; #Abondance d'algue (en μ.mole.N.l.^-1) et à t = 0Z = Z0; #Abondance de Métynnis (en μ.mole.N.l.^-1) et à t = 0T = t; #Matrice dans laquelle on stocke tous les tempsNsol = N; #Matrice dans laquelle on stocke toutes les solutions de NPsol = P; #Matrice dans laquelle on stocke toutes les solutions de PZsol = Z; #Matrice dans laquelle on stocke toutes les solutions de Zfor i = 1:n    t = t + dt;    [dNdt,dPdt,dZdt] = NPZ(N,P,Z)    N = N + dNdt*dt; #On calcul les solutions de N,P,Z pour chaque valeur de dt  P = P + dPdt*dt;  Z = Z + dZdt*dt;    Nsol = [Nsol ; N]; #On stocke toutes les solutions de N,P,Z dans des matrices  Psol = [Psol ; P];  Zsol = [Zsol ; Z];    T = [T ; t];endtocfigure(1) #Toutes les courbes sur un même graphiqueplot(T,Nsol,'b',T,Psol,'g',T,Zsol,'r')legend('Azote','Algue','Metynnis')xlabel('Temps (jour)')ylabel('Concentration en Azote (u.mole.N.l.^{-1})')grid onfigure(2) # 1 Courbe/graphiquesubplot(3,1,1)plot(T,Nsol,'b')xlabel('Temps (jour)')ylabel('Concentration en Azote (u.mole.N.l.^{-1})')grid onsubplot(3,1,2)plot(T,Psol,'g')xlabel('Temps (jour)')ylabel('Concentration en algue (u.mole.N.l.^{-1})')grid onsubplot(3,1,3)plot(T,Zsol,'r')xlabel('Temps (jour)')ylabel('Concentration en Metynnis (u.mole.N.l.^{-1})')grid on